mapper.update:
update RESIDENCERESERV
set available = #{available}
where userId = #{userId}
and itemId = #{itemId}
and availableDate = #{availableDate}
and	availableTime = #{availableTime}

mapper.select:
SELECT userId,availableDate,availableTime,available 
FROM RESIDENCERESERV
WHERE userId = #{userId}
AND availableDate = #{availableDate}
